import React, { useState } from "react";
import './ChatWindow.css';
const Chat = () => {
  const [activeUser, setActiveUser] = useState(null);

  const users = [
    { id: 1, name: "John Doe" },
    { id: 2, name: "Jane Doe" },
    { id: 3, name: "Bob Smith" }
  ];

  const messages = [
    { id: 1, from: "John Doe", message: "Hello Jane!" },
    { id: 2, from: "Jane Doe", message: "Hi John, how are you?" },
    { id: 3, from: "John Doe", message: "I'm doing well, thanks. How about you?" },
    { id: 4, from: "Jane Doe", message: "I'm good too, thanks for asking." }
  ];

  const handleUserClick = (user) => {
    setActiveUser(user);
  };

  return (
    <div className="chat-container">
      <div className="user-list">
        {users.map(user => (
          <div key={user.id} className={`user-item ${activeUser && user.id === activeUser.id ? 'active' : ''}`} onClick={() => handleUserClick(user)}>
            {user.name}
          </div>
        ))}
      </div>
      <div className="message-list">
        {activeUser && messages.filter(message => message.from === activeUser.name).map(message => (
          <div key={message.id} className="message-item">
            <div className="message-sender">{message.from}</div>
            <div className="message-text">{message.message}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chat;
