import React, { useState } from "react";

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      text: "Hello!",
      sender: "bot",
    },
    {
      text: "Hi there!",
      sender: "user",
    },
    {
      text: "How are you?",
      sender: "user",
    },
    {
      text: "I'm good, thanks for asking.",
      sender: "bot",
    },
  ]);

  const [currentChat, setCurrentChat] = useState(messages);

  const handleClick = (index) => {
    const newChat = messages.filter((message) => message.sender === messages[index].sender);
    setCurrentChat(newChat);
  };

  const handleSend = (event) => {
    event.preventDefault();
    const inputText = event.target.elements.message.value;
    const newMessage = {
      text: inputText,
      sender: "user",
    };
    setMessages([...messages, newMessage]);
    event.target.elements.message.value = "";
  };

  return (
    <div className="chat-container">
      <div className="users-container">
        <ul>
          <li onClick={() => setCurrentChat(messages)}>All Chats</li>
          <li onClick={() => handleClick(0)}>Chat with Bot</li>
          <li onClick={() => handleClick(1)}>Chat with User</li>
        </ul>
      </div>
      <div className="messages-container">
        {currentChat.map((message, index) => (
          <div
            key={index}
            className={message.sender === "user" ? "user-message" : "bot-message"}
          >
            <p>{message.text}</p>
          </div>
        ))}
        <form onSubmit={handleSend}>
          <input type="text" name="message" placeholder="Type your message" />
          <button type="submit">Send</button>
        </form>
      </div>
    </div>
  );
};

export default Chat;
