import styled from "styled-components";
// import { messagesList } from "../Data";
import { SearchContainer, SearchInput } from "./ContactListComponents";
const Container = styled.div`
display: flex ;
flex-direction: column;
height: 100%;
flex: 2;
background: #f6f7f8;
`;
const ProfileHeader=styled.div`
    display: flex;
    color: black;
    flex-direction: row;
    background: #ededed;
    padding: 15px;
    align-items: center;
    gap : 10px;
`;

// const ProfileImage =styled.img`
// width: 40px;
// height: 40px;
// border-radius: 50%;
// `;

const ChatBox =styled.div`
display: flex;
background: #f0f0f0;
padding: 10px;
align-items: center;
bottom : 0;
`;


const MessageContainer = styled.div`
display: flex;
flex-direction: column;
height: 100%;
background: #e5ddd6;
`;


const ConversationComponent = ({sharedState}) => {
    return (
    <Container>
        <ProfileHeader>
        {sharedState}
        </ProfileHeader>
        <MessageContainer>
            {/* {messagesList.map((messageData) => (
                <MessageDiv isYours={messageData.senderID === 0}>
                  <Message isYours={messageData.senderID === 0}>{[messageData.text]} </Message>
                </MessageDiv>
            ))} */}
            
        </MessageContainer>
        <ChatBox>
            <SearchContainer>
            <SearchInput placeholder="Type a message"/>
            </SearchContainer>
        </ChatBox>
        </Container>
    );
};
export default ConversationComponent;
