import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  /* Add any styling you want for the container */
`;

const SearchBox = styled.div`
  /* Add any styling you want for the search box */
`;

const SearchContainer = styled.div`
  /* Add any styling you want for the search container */
`;

const SearchIcon = styled.img`
  /* Add any styling you want for the search icon */
`;

const SearchInput = styled.input`
  /* Add any styling you want for the search input */
`;

const UserListItem = styled.p`
  border-bottom: 1px solid black;
  padding-bottom: 10px;
`;

function UserList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Call function to retrieve list of users
    getUsers();
  }, []);

  async function getUsers() {
    try {
      // Perform GET request to retrieve user list data
      const response = await fetch('/api/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <Container>
      <SearchBox>
        <SearchContainer>
          <SearchIcon src={"/search-icon.svg"} />
          <SearchInput placeholder="Search or start new chat" />
        </SearchContainer>
      </SearchBox>
      {users.map(user => (
        <UserListItem key={user.Username}>
          {user.Username}
        </UserListItem>
      ))}
    </Container>
  );
}
