import React, { useState } from "react";

export const Login = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [apiKey, setApiKey] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });
      if (response.ok) {
        const data = await response.json();
        setApiKey(data.apiKey);
        console.log(`API key received: ${data.apiKey}`);
        props.onFormSwitch("chat");
        props.history.push("/chat"); // update URL
      } else if (response.status === 409) {
        setErrorMessage("Invalid username or password.");
      } else {
        console.error("Failed to login");
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleRegisterButtonClick = (e) => {
    e.preventDefault();
    props.onFormSwitch("register");
    props.history.push("/register"); // update URL
  };

  return (
    <div className="auth-form-container">
      <h2>Login</h2>
      <form className="login-form" onSubmit={handleFormSubmit}>
        <label htmlFor="username">Username</label>
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          type="text"
          placeholder="beepboop"
          id="username"
          name="username"
        />
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          placeholder="********"
          id="password"
          name="password"
        />
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <button type="submit">Log In</button>
      </form>
      <button className="link-btn" onClick={handleRegisterButtonClick}>
        Don't have an account? Register here.
      </button>
    </div>
  );
};
