function ContactListComponent() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
      // Call function to retrieve list of users
      getUsers();
    }, []);
  
    async function getUsers() {
      try {
        // Perform GET request to retrieve user list data
        const response = await fetch('https://cabinet.minion.chat.junglesucks.com/users');
        const data = await response.json();
        console.log(data);
        console.log(response);
        setUsers(data);
      } catch (error) {
        console.error(error);
      }
    }
    return (
        <Container>
        <SearchBox>
            <SearchContainer>
                <SearchIcon src={"/search-icon.svg"}/>
                <SearchInput placeholder="Search or start new chat" />
            </SearchContainer>
        </SearchBox>
        {"Users you chat with"}
        {users.map(user => (
            <UserListItem key={user.Username}>
            {user.Username}
          </UserListItem>
          
        ))}
          </Container>
      
    );
  }

