import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  flex: 0.8;
  color: black;
`;

export const SearchButton = styled.button`
  background-color: #007bff;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 20px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #0069d9;
  }
`;


const SearchBox = styled.div`
  display: flex;
  background: #f6f6f6;
  padding: 10px;
`;

const UserListItem = styled.p`
  border-bottom: 1px solid gray;
  padding-bottom: 10px;
`;

export const SearchContainer = styled.div`
  display: flex;
  flex-direction: row;
  background: white;
  border-radius: 16px;
  width: 100%;
  padding: 20px 0;
`;

const SearchIcon = styled.img`
  width: 28px;
  height: 28px;
  padding-left: 10px;
`;

export const SearchInput = styled.input`
  width: 100%;
  outline: none;
  border: none;
  padding-left: 15px;
  font-size: 17px;
  margin-left: 10px;
`;

const ContactListComponent = () => {
  const [users, setUsers] = useState([]);
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    // Call function to retrieve list of users
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      // Perform GET request to retrieve user list data
      const response = await fetch(
        'https://cabinet.minion.chat.junglesucks.com/getUsersIChatWith'
      );
      const data = await response.json();
      console.log(data);
      console.log(response);
      setUsers(data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const search = () => {
    const filteredUsers = users.filter((user) =>
      user.Username.toLowerCase().includes(inputValue.toLowerCase())
    );
    setUsers(filteredUsers);
  };

  return (
    <Container>
      <SearchBox>
        <SearchContainer>
          <SearchIcon src={'/search-icon.svg'} />
          <SearchInput
            placeholder='Search or start new chat'
            value={inputValue}
            onChange={handleInputChange}
          />
          <button onClick={search}>Search</button>
        </SearchContainer>
      </SearchBox>
      {users.map((user) => (
        <UserListItem key={user.Username}>{user.Username}</UserListItem>
      ))}
    </Container>
  );
};

export default ContactListComponent;
