import styled from "styled-components";
import React, { useState, useEffect } from 'react';
// import { messagesList } from "../Data";
import { SearchContainer, SearchInput } from "./ContactListComponents";
const Container = styled.div`
display: flex ;
flex-direction: column;
height: 100%;
flex: 2;
background: #f6f7f8;
`;
const ProfileHeader=styled.div`
    display: flex;
    color: black;
    flex-direction: row;
    background: #ededed;
    padding: 15px;
    align-items: center;
    gap : 10px;
`;

// const ProfileImage =styled.img`
// width: 40px;
// height: 40px;
// border-radius: 50%;
// `;

const ChatBox =styled.div`
display: flex;
background: #f0f0f0;
padding: 10px;
align-items: center;
bottom : 0;
`;


const MessageAndButtonContainer = styled.div`
  display: flex;
  flex: 1;
`;

const ButtonContainer = styled.div`
background: #4caf50;
border: none;
color: white;
padding: 12px 16px;
text-align: center;
text-decoration: none;
font-size: 12px;
border-radius: 12px;
margin-left: 8px;
cursor: pointer;
`;

const MessageContainer = styled.div`
display: flex;
flex-direction: column;
height: 100%;
background: #e5ddd6;
`;


const ConversationComponent = ({sharedState}) => {

  const [setMessages] = useState([]);
//   const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    getMessages();
  });

  const getMessages = async () => {
    try {
      // Perform GET request to retrieve user list data
      const response = await fetch(
        'https://cabinet.minion.chat.junglesucks.com/getMessagesBetweenMeAndUser'
      );
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error(error);
    }
  };

    return ( 
    <Container>
        <ProfileHeader> 
        {sharedState} 
        </ProfileHeader>
        <ChatBox>
        <MessageAndButtonContainer>
      <MessageContainer>
        {
            // get request
        }
      </MessageContainer>
      
    </MessageAndButtonContainer>
    </ChatBox>
            <SearchContainer>
            <ButtonContainer>
        <button>Send</button>
      </ButtonContainer>
            <SearchInput placeholder="Type a message"/>
            </SearchContainer>
       
        </Container>
    );
};
export default ConversationComponent;
